mapper.insert:
insert lamp_statistics(id, lamp_id, region_id, organ_id, is_online, is_fault, is_light, energy, update_time)
values
(#{id}, #{lampId}, #{regionId}, #{organId}, #{isOnline}, #{isFault}, #{isLight}, #{energy}, #{updateTime});

mapper.update:
update lamp_statistics set
lamp_id=#{lampId}, region_id=#{regionId}, organ_id=#{organId}, is_online=#{isOnline}, is_fault=#{isFault}, is_light=#{isLight}, energy=#{electricity}, update_time=#{updateTime},
