mapper.insert:
insert lamp_statistics(id, lamp_id, region_id, organ_id, online, fault, light, electricity, update_time)
values
(#{id}, #{lampId}, #{regionId}, #{organId}, #{online}, #{fault}, #{light}, #{electricity}, #{updateTime});

mapper.update:
update lamp_statistics set
lamp_id=#{lampId}, region_id=#{regionId}, organ_id=#{organId}, online=#{online}, fault=#{fault}, light=#{light}, electricity=#{electricity}, update_time=#{updateTime},
where id=#{id};

mapper.select:
select lamp_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where date_format(update_time,'%Y-%m-%d 00:00:00') = date_sub(date_format(now(),'%Y-%m-%d 00:00:00'), interval 1 day)
and region_id = #{id}
group by lamp_id;

select region_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where date_format(update_time,'%Y-%m-%d 00:00:00') = date_sub(date_format(now(),'%Y-%m-%d 00:00:00'), interval 1 day)
and organ_id = #{id}
group by region_id;

select organ_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where date_format(update_time,'%Y-%m-%d 00:00:00') = date_sub(date_format(now(),'%Y-%m-%d 00:00:00'), interval 1 day)
and organ_id = #{id}
group by organ_id;

select lamp_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where yearweek(update_time) = yearweek(now()) and region_id = #{id}
group by lamp_id;

select region_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where yearweek(update_time) = yearweek(now()) and organ_id = #{id}
group by region_id;

select organ_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where yearweek(update_time) = yearweek(now()) and organ_id = #{id}
group by organ_id;

select lamp_id, avg(online) as online, avg(fault) as fault, avg(light) as light, sum(electricity) as electricity, max(update_time) as update_time
from lamp_statistics
where date_format(update_time,'%Y-%m')=date_format(now(),'%Y-%m') and region_id = #{id}
